# --------------------------------------------------------------- Imports ---------------------------------------------------------------- #

# System
from typing import Optional

# Pip
from jsoncodable import JSONCodable

# ---------------------------------------------------------------------------------------------------------------------------------------- #



# ---------------------------------------------------------- class: BaseProduct ---------------------------------------------------------- #

class BaseProduct(JSONCodable):

    # ------------------------------------------------------------- Init ------------------------------------------------------------- #

    def __init__(
        self,
        title: str,
        asin: str,
        price: float,
        main_image_url: Optional[str] = None
    ):
        self.title = title
        self.asin = asin
        self.price = price
        self.main_image_url = main_image_url


# ---------------------------------------------------------------------------------------------------------------------------------------- #