# --------------------------------------------------------------- Imports ---------------------------------------------------------------- #

# System
from typing import List

# Pip
from jsoncodable import JSONCodable


# ---------------------------------------------------------------------------------------------------------------------------------------- #



# -------------------------------------------------------- class: ProductImageSet -------------------------------------------------------- #

class ProductImageSet(JSONCodable):

    # ------------------------------------------------------------- Init ------------------------------------------------------------- #

    def __init__(
        self,
        asin: str,
        name: str,
        urls: List[str]
    ):
        self.asin = asin
        self.name = name
        self.urls = urls


# ---------------------------------------------------------------------------------------------------------------------------------------- #