# -*- coding: utf-8 -*-

class AppDatameerInstance(object):
	"""
	Details about the Datameer instances.
	"""

	def __init__(self):
		pass;


	"""
	The host IP address.
	"""
	ipAddress = None;

	"""
	The URL of the Datameer GUI.
	"""
	url = None;

	"""
	The ID of the instance associated with the node.
	"""
	instance_id = None;

	"""
	The label of the instance associated with the node.
	"""
	instance_label = None;

	"""
	The schema type
	"""
	type = None;
