# -*- coding: utf-8 -*-

class NetworkOperatingSystem(object):
	"""
	Contains details about the nwtwork operating system of a VolumeTemplate.
	"""

	def __init__(self):
		pass;


	"""
	The operating system type. For example, Ubuntu or CentOS.
	"""
	operating_system_type = None;

	"""
	The version of the operating system.
	"""
	operating_system_version = None;

	"""
	The architecture of the operating system.
	"""
	operating_system_architecture = None;

	"""
	The vendor of the operating system.
	"""
	operating_system_vendor = None;

	"""
	The machine of the operating system.
	"""
	operating_system_machine = None;

	"""
	"""
	operating_system_machine_revision = None;

	"""
	The schema type
	"""
	type = None;
