# -*- coding: utf-8 -*-

class OSTemplateCredentials(object):
	"""
	Information needed to connect to an OS installed by an OSTemplate.
	"""

	def __init__(self):
		pass;


	"""
	User used to connect to the OS installed by the OSTemplate
	"""
	os_template_initial_user = None;

	"""
	Password of the initial_user property.
	"""
	os_template_initial_password = None;

	"""
	Option to use the autogenerated password instead of the one provided in
	os_template_initial_password.
	"""
	os_template_use_autogenerated_initial_password = False;

	"""
	SSH port used to connect to the installed OS.
	"""
	os_template_initial_ssh_port = None;

	"""
	Option to change the initial_user password on the installed OS after deploy.
	"""
	os_template_change_password_after_deploy = None;

	"""
	The schema type
	"""
	type = None;
