class CustomerData:
    def __init__(self, id, vat_number, phone, name, first_name, street, city, zip, subdivision):
        self.id = id
        self.vat_number = vat_number
        self.phone = phone
        self.name = name
        self.first_name = first_name
        self.street = street
        self.city = city
        self.zip = zip
        self.subdivision = subdivision
