class ExpandingMax(BaseEstimator):

    def fit_transform(self, x: np.ndarray) -> np.ndarray:
        exp_max = expanding_max(x)
        self.max = exp_max[-1]
        return exp_max

    def update(self, x: float) -> float:
        if x > self.max:
            self.max = x
        return self.max

